$(window).mousemove(function(e) {
    teste(e);
});

function teste(e) {
    var dde = document.documentElement;
    var movementConstant = .01;
    var ow = ((e.pageX) / (dde.offsetWidth));
    var oh = ((e.pageY) / (dde.offsetHeight));
    $('.parallax .item').each(function(i, el) {
        var y = el.dataset.velocityy;
        var x = el.dataset.velocityx;
        $(el).css('left', ow * (y) + "%");
        $(el).css('top', oh * (x) + "%");
    });
}